import React from 'react'
import Floor from './Floor'
const WIDTH=400, HEIGHT=800, FLOOR_W=80,FLOOR_H=15

class Grid extends React.Component{
  constructor(){
    super()
    this.state={
      'pos':[],
      'doodler':{}
    }
  }
  componentDidMount(){
    let tab=[]
    for(let i=1;i<=5;i++){
      let x=Math.floor(Math.random()*(WIDTH-FLOOR_W))
      let y=130*i;
      tab.push({'x':x,'y':y})
    }
    let doodler=[{'x':tab[tab.length-1].x+FLOOR_W/2,'y':tab[tab.length-1].y-100}]
    this.setState({'pos':tab,'doodler':doodler})
  }
  render(){
    const floors=this.state.pos.map(item => {
      return <Floor key={item.y} x={item.x} y={item.y} />
    });
    return(
      <div className='grid'>
        {floors}
        <Doodler x={this.doodler.x} y={this.doodler.y} />
      </div>
    )
  }
}


export default Grid
