import React from 'react'
import Floor from './Floor'
import Doodler from './Doodler'
const WIDTH=400, HEIGHT=800, FLOOR_W=80,FLOOR_H=15,DOODLER_W=64

class Grid extends React.Component{
  constructor(){
    super()
    this.state={
      'pos':[],
      'doodler':{},
      'doodler_speed':{'vel':0,'acc':1}
    }
    /*setInterval(()=>{
      this.setState((prev)=>{'doodler':{'x':prev.doodler.x,'y':prev.doodler.y+prev.doodler_speed.acc},'doodler_speed':{'vel':prev.doodler_speed.vel+prev.doodler_speed.acc,'acc',prev.doodler_speed.acc+1}})
    },1000)*/
  }
  componentDidMount(){
    setInterval(()=>{
      this.setState(prev=>({'doodler':{'x':prev.doodler.x+10}}))
    },10)
    let tab=[]
    for(let i=1;i<=5;i++){
      let x=Math.floor(Math.random()*(WIDTH-FLOOR_W))
      let y=130*i;
      tab.push({'x':x,'y':y})
    }
    let doodler={'x':tab[tab.length-1].x+FLOOR_W/2-DOODLER_W/2,'y':tab[tab.length-1].y-170}
    this.setState({'pos':tab,'doodler':doodler})
  }
  render(){
    const floors=this.state.pos.map(item => {
      return <Floor key={item.y} x={item.x} y={item.y} />
    });
    return(
      <div className='grid'>
        {floors}
        <Doodler x={this.state.doodler.x} y={this.state.doodler.y} />
      </div>
    )
  }
}


export default Grid
