import React from 'react'

class Doodler extends React.Component{
  constructor(){
    super()
    this.state={
      'left':false,
      'right':false,
    }
  }
  render(){
    if(this.props.left===this.state.left&&this.props.right===this.state.right){}
    else this.setState({'right':this.props.right,'left':this.props.left})
    return(
      <div className='doodler' style={{top:`${this.props.y}px`,left:`${this.props.x}px`,transform:`scaleX(${this.state.left?-1:1})`}}>
      </div>

    )
  }


}

export default Doodler
