import React from 'react'
import Floor from './Floor'
const WIDTH=400, HEIGHT=800, FLOOR_W=80,FLOOR_H=15

class Grid extends React.Component{
  constructor(){
    super()
    this.state={
      'pos':[]
    }
  }
  componentDidMount(){
    for(let i=1;i<=5;i++){
      let x=Math.floor(Math.random()*(WIDTH-FLOOR_W))
      let y=130*i;
      this.setState((prev)=>{
        prev.pos.push({'x':x,'y':y})
        return prev
      })
    }
  }
  render(){
    const floors=this.state.pos.map(item => {
      return <Floor key={item.y} x={item.x} y={item.y} />
    });
    return(
      <div className='grid'>
        {floors}
      </div>
    )
  }
}


export default Grid
